import React, { useState } from 'react';
import axios from 'axios';

const AddTodo = () => {
  const [newTask, setNewTask] = useState('');

  const handleAddTodo = async () => {
    try {
      await axios.post('http://localhost:8000/todos/', { task: newTask });
      // Redirect back to the main page after adding the todo
      window.location.href = '/';
    } catch (error) {
      console.error('Error adding todo:', error);
    }
  };

  return (
    <div className="flex justify-center items-center h-screen">
      <div className="w-96">
        <h2 className="text-2xl font-semibold mb-4 text-gray-800">Add Todo</h2>
        <input
          type="text"
          value={newTask}
          onChange={(e) => setNewTask(e.target.value)}
          className="input input-bordered mb-2"
          placeholder="Enter your todo"
        />
        <button onClick={handleAddTodo} className="btn btn-primary mr-2">
          Add
        </button>
        <a href="/" className="btn">Cancel</a>
      </div>
    </div>
  );
};

export default AddTodo;
