import React, { useState } from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom'; // Import useHistory from react-router-dom

const AddTodoPage = () => {
  const [newTask, setNewTask] = useState('');
  const history = useHistory(); // Get history object

  const handleAddTodo = async () => {
    try {
      const response = await axios.post('http://localhost:8000/todos/', { task: newTask });
      if (response.status === 201) {
        // Redirect back to the Todo page after adding
        history.push('/todos');
      }
    } catch (error) {
      console.error('Error adding todo:', error);
    }
  };

  return (
    <div className="flex justify-center items-center h-screen">
      <div className="w-96">
        <h2 className="text-2xl font-semibold mb-4 text-gray-800">Add Todo</h2>
        <input
          type="text"
          value={newTask}
          onChange={(e) => setNewTask(e.target.value)}
          className="input input-bordered mb-2"
        />
        <button onClick={handleAddTodo} className="btn btn-primary mr-2">
          Add
        </button>
        <button onClick={() => history.goBack()} className="btn">
          Cancel
        </button>
      </div>
    </div>
  );
};

export default AddTodoPage;
