import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FiCheckCircle, FiTrash2, FiPlus } from 'react-icons/fi'; // Import icons from react-icons
import Modal from 'react-modal'; // Import react-modal
import { RiTodoLine } from 'react-icons/ri';

const TodoApp = () => {
  const [todos, setTodos] = useState([]);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [newTask, setNewTask] = useState('');

  useEffect(() => {
    // Fetch todos from API
    axios.get('http://localhost:8000/todos?format=json')
      .then(response => {
        setTodos(response.data);
      })
      .catch(error => {
        console.error('Error fetching todos:', error);
      });
  }, []);

  const handleMarked = async (id) => {
    const updatedTodos = todos.map(todo =>
      todo.id === id ? { ...todo, marked: !todo.marked } : todo
    );
    setTodos(updatedTodos);

    try {
      const todo = updatedTodos.find(todo => todo.id === id);
      const updatedTodo = { ...todo };

      await axios.patch(`http://localhost:8000/todos/${id}/`, updatedTodo);
    } catch (error) {
      console.error('Error marking todo:', error);
    }
  };

  const handleDelete = async (id) => {
    try {
      // Delete the todo
      await axios.delete(`http://localhost:8000/todos/${id}/`);
      // Update the state after deletion
      setTodos(todos.filter(todo => todo.id !== id));
    } catch (error) {
      console.error('Error deleting todo:', error);
    }
  };

  const handleAddTodo = async () => {
    try {
      const response = await axios.post('http://localhost:8000/todos/', { task: newTask });
      if (response.status === 201) {
        setIsModalOpen(false); // Close the modal
        setNewTask(''); // Clear the input
        setTodos([...todos, response.data]); // Add the new todo to the list
      }
    } catch (error) {
      console.error('Error adding todo:', error);
    }
  };

  return (
    <div className="flex justify-center items-center h-screen">
      <div className="w-96">
        <h2 className="text-2xl font-semibold mb-4 text-gray-800">Todo List</h2>
        <button
          className="btn btn-primary mb-2"
          onClick={() => setIsModalOpen(true)}
        >
          <FiPlus className="mr-2" /> Add Todo
        </button>
        <ul className="space-y-2">
          {todos.map(todo => (
            <li
              key={todo.id}
              className={`mb-2 flex justify-between items-center p-2 rounded-md ${
                todo.marked ? 'line-through text-gray-400' : 'bg-gray-100 text-gray-700'
              }`}
            >
              <span>{todo.task}</span>
              <div>
                <button
                  className={`px-2 py-1 rounded-md ${
                    todo.marked ? 'bg-green-500 text-white' : 'bg-blue-500 text-white'
                  }`}
                  onClick={() => handleMarked(todo.id)}
                >
                  <FiCheckCircle size={18} /> {/* Check icon */}
                </button>
                <button
                  className={`px-2 py-1 ml-2 rounded-md ${
                    todo.marked ? 'bg-red-500 text-white' : 'bg-red-500 text-white'
                  }`}
                  onClick={() => handleDelete(todo.id)}
                >
                  <FiTrash2 size={18} /> {/* Trash icon */}
                </button>
              </div>
            </li>
          ))}
        </ul>
      </div>
      <Modal
        isOpen={isModalOpen}
        onRequestClose={() => setIsModalOpen(false)}
        className="modal"
        overlayClassName="overlay"
      >
        <h2>Add Todo</h2>
        <input
          type="text"
          value={newTask}
          onChange={(e) => setNewTask(e.target.value)}
          className="input input-bordered mb-2"
        />
        <button onClick={handleAddTodo} className="btn btn-primary mr-2">
          Add
        </button>
        <button onClick={() => setIsModalOpen(false)} className="btn">
          Cancel
        </button>
      </Modal>
    </div>
  );
};

export default TodoApp;
