import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FiCheckCircle, FiTrash2 } from 'react-icons/fi'; // Import icons from react-icons

const TodoApp = () => {
  const [todos, setTodos] = useState([]);

  useEffect(() => {
    // Fetch todos from API
    axios.get('http://localhost:8000/todos?format=json')
      .then(response => {
        setTodos(response.data);
      })
      .catch(error => {
        console.error('Error fetching todos:', error);
      });
  }, []);

 const handleMarked = async (id) => {
  try {
    const todo = todos.find(todo => todo.id === id);
    const updatedTodo = { ...todo, marked: !todo.marked };

    const response = await axios.patch(`http://localhost:8000/todos/${id}/`, updatedTodo);
    if (response.status === 200) {
      const updatedTodos = todos.map(todo =>
        todo.id === id ? { ...todo, marked: !todo.marked } : todo
      );
      setTodos(updatedTodos);
    }
  } catch (error) {
    console.error('Error marking todo:', error);
  }
};

  const handleDelete = async (id) => {
    try {
      // Delete the todo
      await axios.delete(`http://localhost:8000/todos/${id}/`);
      // Update the state after deletion
      setTodos(todos.filter(todo => todo.id !== id));
    } catch (error) {
      console.error('Error deleting todo:', error);
    }
  };

  return (
    <div className="flex justify-center items-center h-screen">
      <div className="w-96">
        <h2 className="text-2xl font-semibold mb-4 text-gray-800">Todo List</h2>
        <ul>
          {todos.map(todo => (
            <li
              key={todo.id}
              className={`mb-2 flex justify-between items-center p-2 rounded-md ${
                todo.marked ? 'line-through text-gray-400' : 'bg-gray-100 text-gray-700'
              }`}
            >
              <span>{todo.task}</span>
              <div>
                <button
                  className={`px-2 py-1 rounded-md ${
                    todo.marked ? 'bg-green-500 text-white' : 'bg-blue-500 text-white'
                  }`}
                  onClick={() => handleMarked(todo.id)}
                >
                  <FiCheckCircle size={18} /> {/* Check icon */}
                </button>
                <button
                  className={`px-2 py-1 ml-2 rounded-md ${
                    todo.marked ? 'bg-red-500 text-white' : 'bg-red-500 text-white'
                  }`}
                  onClick={() => handleDelete(todo.id)}
                >
                  <FiTrash2 size={18} /> {/* Trash icon */}
                </button>
              </div>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default TodoApp;
